function BorderWizard(options) {
  let images = document.querySelectorAll('.shadowizard')

  if (options.borderType === 'color')
    options.borderType = 'green'
  else
    options.borderType = 'black'

  images.forEach(image => {
    image.style.border = `1px solid ${options.borderType}`;
    if (options.padding) image.style.padding = '1em'
  })
}

module.exports.BorderWizard = BorderWizard